<script>
	import * as _ from 'lamb';

	import {_dataset} from 'app/stores/dataset';
	import {_focusedOrg, _focusedOrgId} from 'app/stores/interaction';

	export let getLonLat = _.identity;
	export let items = [];
	export let projectFn = _.identity;

	$: projectItem = item => projectFn(getLonLat(item));
	$: markers = projectFn && getLonLat && _.map(items, projectItem) || [];
	$: focusedMarker =
		$_focusedOrg && projectFn && getLonLat &&
		projectFn(getLonLat($_focusedOrg));
</script>

<g class='SvgMarkers'>
	{#each markers as {x, y}}
		<circle
			class='marker'
			cx={x}
			cy={y}
			r=2
		/>
	{/each}
	{#if focusedMarker}
		<circle
			class='marker focused'
			cx={focusedMarker.x}
			cy={focusedMarker.y}
			r=6
		/>
	{/if}
</g>

<style>
	.marker {
		fill-opacity: 0.75;
		cursor: pointer;
	}

	.focused {
		fill: red;
	}
</style>
