<script>
	import * as _ from 'lamb';

	import {MIN_SCORE} from 'app/config';
	import {_minScore} from 'app/stores/topics';
</script>

<div class='Settings'>
	<div class='panel'>
		<h3>Topics</h3>
		<div class='item'>
			<label for='minScore'>Min score</label>
			<input
				bind:value={$_minScore}
				id='minScore'
				list='tickmarks'
				max=100
				min={MIN_SCORE}
				step=10
				type='range'
			>
			<datalist id='tickmarks'>
				{#each _.range(MIN_SCORE, 110, 10) as score}
					<option value={score}></option>
				{/each}
			</datalist>
		</div>
	</div>
</div>

<style>
	.Settings {
		width: 100%;
		height: 100%;
		padding: 1em;
		display: grid;
	}

	.item {
		align-items: center;
		display: flex;
		gap: 1em;
	}

	.item input {
		flex: 1;
	}
</style>
